package JavaBasics;

public class LoopConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		for(int i =1; i<=10;i++)
		{
			System.out.println(i);
		}
		
		// print 10 to 0
		System.out.println("***********");
		
		for (int k =10;k>=1;k--) 
		{
			System.out.println(k);
	}
		
		// print 10 to 0 and 0 to -10
		
		for(int j=10;j>=-10;j--)
		{
			System.out.println(j);
		}
	}
	
}
